import { Component } from 'react'

class Map extends Component {
    componentDidMount(){
        var {AMap}= window

        const map= new AMap.Map('map',{
            center:[114,35],
            zoom:12
        })

        AMap.plugin('AMap.Geocoder',function(){
            var geocoder = new AMap.Geocoder({
                city:'全国'
            })

            geocoder.getLocation('侯马',function(status,result){
                if(status==='complete' && result.info==='OK'){
                    let {lng,lat} =result.geocodes[0]
                    console.log([lng,lat])
                }
            })
        })

        const marker= new AMap.Marker({
            position:[114,35],
            title:'村里'
        })

        map.add(marker)
    }

    render(){
        return null
    }
}

export default Map
