import React,{ Component } from 'react';
import { Link } from 'react-router-dom'
import './weather.css';

class Weather extends Component {
    constructor(){
        super();
        this.state={
            date:[{city:'新绛'}]
        }
    }

    componentDidMount(){
        let weather;
        fetch('https://www.tianqiapi.com/api/?version=v6&city=侯马')
        .then((response)=>{
            return response.json()
        })
        .then((data)=>{
            weather=data
            console.log(weather)
            this.setState({
                date:[weather]
        })
        })
        .catch(error=>console.log(error))
    }

    render(){
        let wea_Date=this.state.date[0] 
        return <div className='container'>
        <div className='button'>
            <Link className='search' to='/search'>search</Link>
            <Link className='location' to='/location'>location</Link>
        </div>
        <div className='weather-container'>
            <div className='detail'>
                <p>{wea_Date.city}</p>
                <p>6月21日，周五 23:00</p>
                <p>22℃</p>
                <p>23/20℃ 雨</p>
            </div>
        </div>
        <div className='message'>
            <div className='gas-index'>
                <div className='AQI'>
                    <p>AQI</p>
                    <p>良</p>
                    <p>23</p>
                </div>
                <div className='AQI'>
                        <p>AQI</p>
                        <p>良</p>
                        <p>23</p>
                </div>
            </div> 
        </div>
    </div>
    }
}

export default Weather
