import React,{Component} from 'react'
import { Route } from 'react-router-dom'
import Weather from './weather'
import Search from './search'
import Location from './location'
import { tsConstructorType } from '@babel/types';

class App extends Component {

  constructor(){
    super()
    this.state={
      locations:[{city:'xinjiang',lngLat:[115,45]}],
      weather:[{}]
    }
    this.addLocation=this.addLocation.bind(this)
    this.addWeather=this.addWeather.bind(this)
  }

  addWeather(lnglat){
      fetch(`https://www.tianqiapi.com/api/?version=v6&city=${lnglat}`)
           .then((response)=>{return response.json()})
           .then(data=>{this.setState((preState)=>(preState.weather.push(data)))})
           .catch(error=>console.log(error))
  }

  addLocation(city){
      let loca={city:city}
      console.log(loca)
      this.setState((preState)=>preState.locations.push(loca))
  }
  render(){
    const {locations}=this.state
    return (
      <div className="App">
           <Route exact path='/' render={()=><Weather></Weather>}></Route>
           <Route exact path='/search' render={()=><Search addLocation={this.addLocation}></Search>}></Route>
           <Route exact path='/location' render={()=><Location locations={locations} ></Location>}></Route>
      </div>
    );
  }

}

export default App;
