import React,{ Component } from 'react'

class Map extends Component {
    componentDidMount(){
        var {AMap}= window

        const map= new AMap.Map('map',{
            center:[114,35],
            zoom:12
        })

        const marker= new AMap.Marker({
            position:[114,35],
            title:'村里'
        })

        map.add(marker)
    }

    render(){
        return null
    }
}

export default Map
