import React ,{ Component } from 'react';
import { Link } from 'react-router-dom'
import Map from './map'
import {addMarker} from './config'

class Search extends Component {
    
    constructor(){
        super()
        this.state={
            searchTerm:'',
            show:false
        }
        this.show=this.show.bind(this)
        this.hide=this.hide.bind(this)
    }

    updateSearchTerm(event){
        this.setState({searchTerm:event.target.value})
    }

    show(){
        this.setState({show:true})
    }

    hide(){
        this.setState({show:false})
    }

    render(){
        const { addLocation , addWeather }=this.props
        const {searchTerm,show} =this.state

        return <div className='search-page'>
                   <div className='search-container'>
                       <div className='combine'>
                            <Link to='/'>返回</Link>
                            <input type='text' 
                                   value={this.searchTerm} 
                                   onChange={(event)=>{
                                   this.setState({searchTerm:event.target.value})
                                }}/> 
                       </div>
                       <div  className='locate' onClick={
                            ()=>{addMarker(searchTerm).then((data)=>{console.log(data);this.show()}).catch((data)=>console.log(data))}
                        }>dingwei</div>
                   </div>
                   <div id='map'>
                       <Map></Map>
                       {show?<div className='add-location'>
                           <span className='city-name'>{searchTerm}</span>
                           <span className='add-buttom' onClick={()=>{addLocation(searchTerm);addWeather(searchTerm)}} ><Link to='/location'>add</Link></span>
                       </div>:show===false}
                   </div>
               </div>
    }
}

export default Search
