import {map} from './map.js'

let { AMap }= window


let geocoder = new AMap.Geocoder({
    city:'全国'
})

export function getLngLat(searchT){
    let pro = new Promise(function(resolve,reject){
        geocoder.getLocation(searchT,function(status,result){
            if(status==='complete' && result.info==='OK'){
                let {lng,lat} =result.geocodes[0].location;
                let lnglat=[lng,lat];console.log([lng,lat])
                resolve(lnglat)    
            }else{
                reject(status)
            }
        })   
    })

    return pro
}

export function addMarker(difang,show){

    geocoder.getLocation(difang,function(status,result){
        if(status==='complete' && result.info==='OK'){
            let {lng,lat} =result.geocodes[0].location;
            let marker= new AMap.Marker({
                position:[lng,lat],
                title:difang 
            })
            map.add(marker)
            show()
            console.log([lng,lat],result.geocodes)
            
        }else{
            throw Error(status)
        }
    })
}

export default AMap
